sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
g_form.clearValue('priority');

//alert("You have changed Urgency from " + oldValue + " to " + newValue + ".");
//	jslog("Impact is " + g_form.getValue('u_impact'));

	// Set the priority field based on the impact and urgency fields
   if (g_form.getValue ('u_urgency') == 'lowUrgency') {  
	   jslog("Urgency is " + g_form.getValue('u_urgency'));
		if (g_form.getValue ('u_impact') == 'lowImpact') {
		   g_form.setValue('priority', 5);
		}
	   	if (g_form.getValue ('u_impact') == 'mediumImpact') {
		   g_form.setValue('priority', 4);
		}
	   	if (g_form.getValue ('u_impact') == 'largeImpact') {
		   g_form.setValue('priority', 3);
		}
   }

	if (g_form.getValue ('u_urgency') == 'mediumUrgency') {  
		if (g_form.getValue ('u_impact') == 'lowImpact') {
		   g_form.setValue('priority', 4);
		}
	   	if (g_form.getValue ('u_impact') == 'mediumImpact') {
		   g_form.setValue('priority', 3);
		}
	   	if (g_form.getValue ('u_impact') == 'highImpact') {
		   g_form.setValue('priority', 2);
		}
   }

	if (g_form.getValue ('u_urgency')  == 'highUrgency') {  
		if (g_form.getValue ('u_impact') == 'lowImpact') {
		   g_form.setValue('priority', 3);
		}
	   	if (g_form.getValue ('u_impact') == 'mediumImpact') {
		   g_form.setValue('priority', 2);
		}
	   	if (g_form.getValue ('u_impact') == 'highImpact') {
		   g_form.setValue('priority', 1);
		}
   }
}
