sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
	// set variables

    },

isDatePast: function(strDate){
	// if the desiredDate is before todaysDate, retrun true, else return flase
	var todaysDateTime = new GlideDateTime();
	var desiredDateTime = new GlideDateTime(strDate);

	var todaysDate = todaysDateTime.getLocalDate();
	var desiredDate = desiredDateTime.getLocalDate();

	if (desiredDate.before(todaysDate)) {
		return true;
	}
	else {
		return false;
	}
},
isDateToday: function(strDate){
	// if the testdate is equals todaysDate, retrun true, else return flase
	var todaysDateTime = new GlideDateTime();
	var desiredDateTime = new GlideDateTime(strDate);

	var todaysDate = todaysDateTime.getLocalDate();
	var desiredDate = desiredDateTime.getLocalDate();

	if (desiredDate.equals(todaysDate)) {
		return true;
	}
	else {
		return false;
	}
},
    type: 'NeedItUtils'
};
